import React ,{useState}from 'react';
import { Alert, TextInput } from 'react-native';

import {
  StyleSheet,
  ImageBackground,
  Dimensions,
  StatusBar,
  TouchableWithoutFeedback,
  Keyboard,
  ScrollView
} from 'react-native';
import { Block, Checkbox, Text, Button as GaButton, theme } from 'galio-framework';

import { Button, Icon, Input } from '../components';
import { Images, nowTheme } from '../constants';
import { useDispatch } from 'react-redux';
import { actions } from '../plugin/redux/action';

const { width, height } = Dimensions.get('screen');

const DismissKeyboard = ({ children }) => (
  <TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>{children}</TouchableWithoutFeedback>
);
function validate(data,type){
  let result;
  switch(type){
    case username:
      if(!data){
        result = {
          msg: "username cannot be empty!"
        }
      }
  }
}
function Register(props){

    const dispatch = useDispatch();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [borderColor, setColor] = useState({username:'#E3E3E3',email:'#E3E3E3',password:'#E3E3E3'});
    const [error ,setError ] = useState(false);
    console.log(borderColor);

    function checkError(con,text){
      if(con ){
        var x;
        for (x in borderColor){
          if(x == text){
            borderColor[x] = '#ff0000';
          }
        }
        setColor(borderColor);
        setError(true);
      }else{
        var x;
        for (x in borderColor){
          if(x == text){
            borderColor[x] = '#E3E3E3';
          }
        }
        setError(false);
      }
    }




    const [email, setEmail] = useState("");
    const { navigation } = props;

    const Results = () => (
      <div id="results" className="search-results">
        Some Results
      </div>
    )
    function checkInfo(text){
      let inquireInfo;
      switch (text){
        case "username":
          inquireInfo = username;
          break;
        case "email":
          inquireInfo = email;
          break;
        default:
          break;
      }
      if(inquireInfo.length > 0){
        actions.getAccountInfo(inquireInfo,text)
        .then(result=>{
          console.log(result);
          if(!result.status){
            checkError(true,text)
          }else{
            checkError(false,text)
          }
        }).catch(err=>{
          console.error(err);
        });

      }

    }
    function handleSubmit(e) {
      // e.preventDefault();
      console.log("running handle submit")
      // setSubmitted(true);
      console.log(username,password,email);
      if(error){
        Alert.alert("Register Error","Make Appeant Change first")
      }
      else if (username && password && email) {
          // get return url from location state or default to home page
          // const { from } = location.state || { from: { pathname: "/" } };
          dispatch(actions.register(username, password,email  ));
      }
    }
    return (

      <DismissKeyboard>
        <Block flex middle>
          <ImageBackground
            source={Images.RegisterBackground}
            style={styles.imageBackgroundContainer}
            imageStyle={styles.imageBackground}
          >
            <Block flex middle>

              <Block style={styles.registerContainer}>
                <ScrollView
                showsVerticalScrollIndicator={false}
                contentContainerStyle={styles.articles}
                >
                  <Block flex space="evenly">
                    <Block flex={0.4} middle style={styles.socialConnect}>
                      <Block flex={0.5} middle>
                        <Text
                          style={{
                            fontFamily: 'montserrat-regular',
                            textAlign: 'center'
                          }}
                          color="#333"
                          size={24}
                        >
                          Register
                        </Text>
                      </Block>

                      <Block flex={0.5} row middle space="between" style={{ marginBottom: 18 }}>
                        <GaButton
                          round
                          onlyIcon
                          shadowless
                          icon="twitter"
                          iconFamily="Font-Awesome"
                          iconColor={theme.COLORS.WHITE}
                          iconSize={theme.SIZES.BASE * 1.625}
                          color={nowTheme.COLORS.TWITTER}
                          style={[styles.social, styles.shadow]}
                        />

                        <GaButton
                          round
                          onlyIcon
                          shadowless
                          icon="dribbble"
                          iconFamily="Font-Awesome"
                          iconColor={theme.COLORS.WHITE}
                          iconSize={theme.SIZES.BASE * 1.625}
                          color={nowTheme.COLORS.DRIBBBLE}
                          style={[styles.social, styles.shadow]}
                        />
                        <GaButton
                          round
                          onlyIcon
                          shadowless
                          icon="facebook"
                          iconFamily="Font-Awesome"
                          iconColor={theme.COLORS.WHITE}
                          iconSize={theme.SIZES.BASE * 1.625}
                          color={nowTheme.COLORS.FACEBOOK}
                          style={[styles.social, styles.shadow]}
                        />
                      </Block>
                    </Block>
                    <Block flex={0.1} middle>
                      <Text
                        style={{
                          fontFamily: 'montserrat-regular',
                          textAlign: 'center'
                        }}
                        muted
                        size={16}
                      >
                        or be classical
                      </Text>
                    </Block>
                    <Block flex={1} middle space="between">
                      <Block center flex={0.9}>
                        <Block flex space="between">
                          <Block>
                            <Block width={width * 0.8} style={{ marginBottom: 5 }}>
                              <Input onRef={input => { this.textInput = input }}
                                placeholder="Username"
                                style={
                                  {borderWidth: 1,
                                  borderColor: borderColor.username,
                                  borderRadius: 21.5}
                                }
                                onBlur={event=>checkInfo("username")}
                                onChangeText={text => setUsername(text)}
                                iconContent={
                                  <Icon
                                    size={16}
                                    color="#ADB5BD"
                                    name="profile-circle"
                                    family="NowExtra"
                                    style={styles.inputIcons}
                                  />
                                }
                              />
                            </Block>
                            {/* { showResults ? <Results /> : null } */}
                            <Block width={width * 0.8}>
                              <Input
                                placeholder="Email"
                                style={
                                  {borderWidth: 1,
                                  borderColor: borderColor.email,
                                  borderRadius: 21.5}
                                }
                                onBlur={event=>checkInfo("email")}
                                onChangeText={text => setEmail(text)}
                                iconContent={
                                  <Icon
                                    size={16}
                                    color="#ADB5BD"
                                    name="email-852x"
                                    family="NowExtra"
                                    style={styles.inputIcons}
                                  />
                                }
                              />
                            </Block>

                            <Block width={width * 0.8} style={{ marginBottom: 5 }}>
                              <Input
                                placeholder="Password"
                                sstyle={
                                  {borderWidth: 1,
                                  borderColor: borderColor.password,
                                  borderRadius: 21.5}
                                }
                                onBlur={event=>checkInfo("password")}
                                onChangeText={text => setPassword(text)}
                                iconContent={
                                  <Icon
                                    size={16}
                                    color="#ADB5BD"
                                    name="caps-small2x"
                                    family="NowExtra"
                                    style={styles.inputIcons}
